const initialData = {
    games : [],

};

export const gamesReducer = (state=initialData , action)=>{

     switch(action.type)
     {
         case 'GET_ALL_GAMES' : {
             return{
                 ...state,
                 games : action.payload
             }
         }
         
         default:return state
     }

}

