import axios from "axios";
import { message } from "antd";
export const bookGame = (reqObj) => async (dispatch) => {
  dispatch({ type: "LOADING", payload: true });
  console.log(reqObj)

  try {
     await axios.post("/api/bookings/bookgame" , reqObj);

    dispatch({ type: "LOADING", payload: false });
    message.success("Your game booked successfully");
    setTimeout(() => {
      window.location.href='/userbookings'
    }, 500);

  } 
  catch (error) {
    console.log(error);
    dispatch({ type: "LOADING", payload: false });
    message.error("Something went wrong , please try later");
  }
};

export const bookGame1 = (reqObj) => async (dispatch) => {
  dispatch({ type: "LOADING", payload: true });
